public class Value_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double distance=15E7;
		final double speed=3E5;
		double time=distance/speed;
		System.out.println("태양에서 지구까지 거리: 약 1억 5천만 km" );
		System.out.println("빛의 속도: 30만 km");
		System.out.println("빛이 태양에서 출발하여 지구에 도달하는 시간은: " + (int)time+ "초");
	}

}
